e>';    extract(shortcode_atts(array(        'tabletitle' => '',        'slider_type' => '',        'currency' => '',        'btn_txt' => '',        'period_text' => '',        'opt1name' => '',        'image1url' => '',        'opt2name' => '',        'image2url' => '',        'opt3name' => '',        'image3url' => '',        'opt4name' => '',        'image4url' => '',        'coupon_code' => '',        'coupon_text' => '',        'coupon_code_value' => '',    ), $atts));    //echo 'content: <pre>'.print_r($content,true).'</pre>';    $listSortCode = explode('][', $content);    $out = array();    foreach ($listSortCode as $key => $singleShortCode) {        if ($key == 0) {            $out[] = json_decode(do_shortcode($singleShortCode . ']'), true);        } elseif ($key == count($listSortCode) - 1) {            $out[] = json_decode(do_shortcode('[' . $singleShortCode), true);        } else {            $out[] = json_decode(do_shortcode('[' . $singleShortCode . ']'), true);        }    }    //echo 'out: <pre>'.print_r($out,true).'</pre>';    foreach ($out as $key => $arr) {        $values['opt1arr'][] = $arr['opt1value'];        $values['opt2arr'][] = $arr['opt2value'];        $values['opt3arr'][] = $arr['opt3value'];        $values['opt4arr'][] = $arr['opt4value'];        $values['planPrice'][] = $arr['plan_price'];        $values['planDprice'][] = $arr['discount_price'];        $values['PlanName'][] = $arr['plan_name'];        $values['planOrderLink'][] = $arr['plan_order_link'];    }    $slider_data = '<div class="vps-plans text-center col-xs-12 ' . (!empty($slider_type) ? $slider_type . '_slider' : 'horizontal_slider') . '">       <div class="slider-container">       <div class="slider_box col-lg-3 col-md-3 col-sm-12 col-xs-12">';    if (!empty($slider_type)) {        $slider_data .= '<div id="slider"></div>           <div class="price_rangetxt">';        $nameAR = array_reverse($values['PlanName']);        foreach ($nameAR as $key => $name) {            $slider_data .= '<div id="icon-' . (count($nameAR) - ($key + 1)) . '" class="icon ">' . $name . '</div>';        }        $slider_data .= '</div></div>';    } else {        $slider_data .= '           <div class="price_rangetxt">';        $nameAR = $values['PlanName'];        foreach ($nameAR as $key => $name) {            $slider_data .= '<div id="icon-' . ($key) . '" class="icon ">' . $name . '</div>';        }        $slider_data .= '</div><div id="slider"></div></div>';    }    $image_url1 = wp_get_attachment_image_src($image1url);    $image_url2 = wp_get_attachment_image_src($image2url);    $image_url3 = wp_get_attachment_image_src($image3url);    $image_url4 = wp_get_attachment_image_src($image4url);    $planOrderLink = (!empty($planOrderLink)) ? $planOrderLink : '';    $slider_data .= '<div class="price-slider-rt col-lg-9 col-md-9 col-sm-12 col-xs-12">           <div class="priceslider_content">           <aside class="smalbox1">               <figure><span><img src="' . $image_url1[0] . '" alt="" /></span></figure>    ' . $opt1name . '<br>               <span id="opt1Val" class="numeric1"></span>               <!--smalbox1-->           </aside>           <aside class="smalbox1">               <figure><span><img src="' . $image_url2[0] . '" alt="" /></span></figure>    ' . $opt2name . '<br>               <span id="opt2Val" class="numeric1"></span>               <!--smalbox1-->           </aside>           <aside class="smalbox1">               <figure><span><img src="' . $image_url3[0] . '" alt="" /></span></figure>    ' . $opt3name . '<br>               <span id="opt3Val" class="numeric1"></span>               <!--smalbox1-->           </aside>           <aside class="smalbox1">               <figure><span><img src="' . $image_url4[0] . '" alt="" /></span></figure>    ' . $opt4name . '<br>               <span id="opt4Val" class="numeric1"></span>               <!--smalbox1-->           </aside>                               <div class="price-greybox col-xs-12">            <div id="price_wrap"></div>             <div class="price-button"><a class="buynow-button" href="' . $planOrderLink . '">' . $btn_txt . '</a></div>';    if (!empty($coupon_code) && !empty($coupon_code_value)) {        $slider_data .= '<div class="price_offer"><span class="off"> ' . $coupon_code_value . '</span> <br> ' . $coupon_text . ': <span> ' . $coupon_code . '</span></div> <!--priceslider_content-->       </div> ';    }    $slider_data .= '</div>       </div>   <!--Linux-->   </div>	</div>    <!--vps-plans-->    </div>';    foreach ($values['planOrderLink'] as $key => $val) {        if (strcmp($val, '&amp;') > 0) {            $val1 = str_replace('&amp;', '&', $val);            $planOrderLink .= '"' . $val1 . '"';        } else {            $planOrderLink .= '"' . $val . '"';        }        if ($key == 0 || $key < count($values['planOrderLink'])) {            $planOrderLink .= ',';        }    }    $planOrderLink = substr($planOrderLink, 0, -1);    $slider_data .= '    <script>     var opt1arr = new Array("' . implode('","', $values['opt1arr']) . '");var opt2arr = new Array("' . implode('","', $values['opt2arr']) . '");var opt3arr = new Array("' . implode('","', $values['opt3arr']) . '");var opt4arr = new Array("' . implode('","', $values['opt4arr']) . '");var price_arr = new Array("' . implode('","', $values['planPrice']) . '");var discount_price_arr = new Array("' . implode('","', $values['planDprice']) . '");var link_arr = new Array(' . $planOrderLink . ');var minSliderVal=1;var maxSliderVal=' . count($values['planOrderLink']) . ';var sliderType="' . (!empty($slider_type) ? $slider_type : '1') . '";var currency="' . (!empty($currency) ? $currency : '$') . '";var period_text="' . (!empty($period_text) ? $period_text : 'mo') . '";</script>';    wp_enqueue_script('jquery-ui', plugin_dir_url(__FILE__) . '.